import logo from "../image/logo.png";
import "./NavBar.css";
import React, { useState, useEffect, useRef } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faBars, faCircleXmark } from "@fortawesome/free-solid-svg-icons";

function NavBar() {
    const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);
    const mobileMenuRef = useRef(null);

    const handleClickOutside = (event) => {
        // Close mobile menu if click is outside
        if (mobileMenuRef.current && !mobileMenuRef.current.contains(event.target)) {
            setIsMobileMenuOpen(false);
        }
    };

    useEffect(() => {
        document.addEventListener('mousedown', handleClickOutside);
        return () => document.removeEventListener('mousedown', handleClickOutside);
    }, []);

    return (
        <>
            <nav className="relative z-50 shadow-md h-24 border-4 border-[#b6d023] bg-white">
                <div className="flex items-center justify-between px-6 h-full">
                    {/* Logo */}
                    <a href="/" className="flex items-center">
                        <img className="h-16 md:h-24" src={logo} alt="Logo" />
                    </a>

                    {/* Hamburger menu for mobile */}
                    <button
                        onClick={() => setIsMobileMenuOpen(!isMobileMenuOpen)}
                        className="text-black md:hidden focus:outline-none">
                        <FontAwesomeIcon icon={faBars} className="text-2xl" />
                    </button>

                    {/* Desktop Navigation */}
                    <div className=" md:flex items-center space-x-8">
                        <a href="/" className="hover:text-blue-400 transition-transform duration-300">Accueil</a>
                        <a href="/aboutus" className="hover:text-blue-400 transition-transform duration-300">A propos</a>
                        <a href="/services" className="hover:text-blue-400 transition-transform duration-300">Nos Services</a>
                        <a href="/partners" className="hover:text-blue-400 transition-transform duration-300">Nos Références</a>
                        <a href="/contactus" className="hover:text-blue-400 transition-transform duration-300">Contactez-nous</a>
                        <a href="/news" className="hover:text-blue-400 transition-transform duration-300">Actualités</a>
                        <a href="tel:+22962315172" className="bg-[#b6d023] text-black rounded-full px-4 py-2">+229 62 31 5172</a>
                    </div>
                </div>

                {/* Mobile Menu */}
                {isMobileMenuOpen && (
                    <div ref={mobileMenuRef} className="md:hidden fixed inset-0 bg-white bg-opacity-90 z-40 flex flex-col items-center space-y-6 pt-24">
                        <button
                            onClick={() => setIsMobileMenuOpen(false)}
                            className="absolute top-8 right-8 text-3xl text-black">
                            <FontAwesomeIcon icon={faCircleXmark} />
                        </button>
                        <a href="/" className="text-xl hover:text-blue-400" onClick={() => setIsMobileMenuOpen(false)}>Accueil</a>
                        <a href="/aboutus" className="text-xl hover:text-blue-400" onClick={() => setIsMobileMenuOpen(false)}>A propos</a>
                        <a href="/services" className="text-xl hover:text-blue-400" onClick={() => setIsMobileMenuOpen(false)}>Nos Services</a>
                        <a href="/partners" className="text-xl hover:text-blue-400" onClick={() => setIsMobileMenuOpen(false)}>Nos Références</a>
                        <a href="/contactus" className="text-xl hover:text-blue-400" onClick={() => setIsMobileMenuOpen(false)}>Contactez-nous</a>
                        <a href="/news" className="text-xl hover:text-blue-400" onClick={() => setIsMobileMenuOpen(false)}>Actualités</a>
                        <a href="tel:+22962315172" className="text-xl bg-[#b6d023] text-black rounded-full px-6 py-2">+229 62 31 5172</a>
                    </div>
                )}
            </nav>

            {/* Spacer below navbar */}
            <div className="mt-24 md:mt-32"></div>
        </>
    );
}

export default NavBar;
