import Navbar from "../components/Navbar";
import logo from "../image/logo.png";
import { Link} from "react-router-dom";
import aspirateur from "../image/Assets Mockup Cipa's site/apirateur.png";
import thumbsUp from "../image/Assets Mockup Cipa's site/thumbsUpWoman.png";
import rectangle from "../image/rectangle.png";
import partners from "../image/Assets Mockup Cipa's site/partenaires CIPA 1.png";
import maskman from "../image/Assets Mockup Cipa's site/Mask group.png";
import blackwoman from "../image/Flyer service de nettoyage promotion bleu.png";
import company from '../image/company.gif';
import indstry from '../image/industry.gif';
import desherbage from '../image/désherbage.gif';
import consultaion from '../image/consultation.gif';
import products from '../image/products.gif';
import React, { useEffect, useState } from 'react';
import "./LandingPage.css";
import "./MyComponent";
// import { useScroll } from './useScroll';

export default function LandingPage() {
  return (
    <div className="container mx-auto px-4">
      {/* Header */}
      <header className="bg-blue-500 p-6 text-white text-center md:text-left">
        <h1 className="text-3xl font-bold">Responsive Page</h1>
        <p className="mt-2 text-lg">Adaptable layout with Tailwind CSS</p>
      </header>

      {/* Main Content */}
      <main className="flex flex-wrap">
        {/* Left Sidebar */}
        <aside className="w-full md:w-1/4 p-4 bg-gray-100">
          <h2 className="text-xl font-semibold">Sidebar</h2>
          <ul className="mt-4">
            <li className="mb-2">Link 1</li>
            <li className="mb-2">Link 2</li>
            <li className="mb-2">Link 3</li>
          </ul>
        </aside>

        {/* Main Section */}
        <section className="w-full md:w-3/4 p-4">
          <h2 className="text-2xl font-semibold mb-4">Main Content</h2>
          <p className="text-gray-700">
            This main section will stack under the sidebar on smaller screens and sit next to it on larger screens.
          </p>
          <div className="mt-4 grid gap-4 sm:grid-cols-2 md:grid-cols-3">
            <div className="bg-white p-4 border rounded shadow">Item 1</div>
            <div className="bg-white p-4 border rounded shadow">Item 2</div>
            <div className="bg-white p-4 border rounded shadow">Item 3</div>
          </div>
        </section>
      </main>

      {/* Footer */}
      <footer className="bg-lime-200 w-full p-6 md:p-20 text-black grid grid-cols-1 md:grid-cols-4 gap-10">
        <p>&copy; 2024 Responsive Page. All rights reserved.</p>
      </footer>
    </div>
  );
}
